import React from 'react';
import ApiService from '../../services/FilmApiService';

export  default class RandomFilmView extends  React.Component{

    api = new ApiService();

    state={
        id:     null,
        title:  null,
        duration:   null,
        genre:  null,
        description: null,
        year:   null,
        director:   null,
        urlImg:     null,
        urlTrailer: null,
        showed: false
    };

