import React from 'react';
import FilmMiniView from '../FilmMiniView';
import FilmView from '../FilmView';

import './FilmComponent.css';

export  default class FilmComponent extends  React.Component{
    state={
        id:     null,
        title:  null,
        duration:   null,
        genre:  null,
        description: null,
        year:   null,
        director:   null,
        urlImg:     null,//'https://www.okino.ua/media/var/news/2019/01/11/avengers-infinity-war-poster.jpg',
        urlTrailer: null,
        showed: false,
        mini: false
    };

    onButtonClick = () => {
        const result = this.props.filmFunc();
        console.log(result);
        this.setState({
            id: result.id,
            title: result.title,
            duration: result.duration,
            genre: result.genre,
            description: result.description,
            year: result.year,
            director: result.director,
            urlImg: result.urlImg,
            urlTrailer: result.urlTrailer,
            showed: true,
            mini: false
        });
    };

    OpenFullView = () => {
        this.setState(() => {})
    };

    render(){

        return (
            <div>
                {this.state.showed?
                    (
                        <div className="FilmComponent">
                            {this.state.mini?(
                                    <FilmMiniView
                                        film={this.state}
                                        /*film={{ title,
                                                urlImg} = this.state}*/
                                        FilmViewClick={this.OpenFullView}/>
                                ):(
                                    //<FilmView film={{title, duration, genre, description, year, director, urlImg, urlTrailer} = this.state}/>
                                    <FilmView film={this.state}/>
                                )}
                            <div className="FilmComponent-Flex">
                                <button id="GetFilmButton" onClick={this.props.FilmSelectFunc}>Еще фильм</button>
                            </div>
                        </div>
                    ):(
                        <div className="FilmComponent-Flex">
                            <button id="GetFilmButton" onClick={this.props.FilmSelectFunc}>Получить фильм</button>
                        </div>
                    )
                }
            </div>
        )
    }
}
