import React from 'react';
import ApiService from '../../services/FilmApiService';

export  default class RandomFilmView extends  React.Component{

    api = new ApiService();

    state={
        id:     null,
        title:  null,
        duration:   null,
        genre:  null,
        description: null,
        year:   null,
        director:   null,
        urlImg:     null,
        urlTrailer: null,
        showed: false
    };

    onButtonClick = () => {
        //Работющий запрос
        fetch("http://localhost:64303/api/Films/Random", {
            method: 'GET',
            mode: 'cors'})

            .then(res => res.json())
            .then((result) => {
                    this.setState({
                            id: result.id,
                            title: result.title,
                            duration: result.duration,
                            genre: result.genre,
                            description: result.description,
                            year: result.year,
                            director: result.director,
                            urlImg: result.urlImg,
                            urlTrailer: result.urlTrailer,
                            showed: true
                    });
                    console.log(result);
                    console.log(this.state);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    console.log(error);
                }
            )

        /*const data = this.api.getRandomFilm();
        this.setState((state) => {
                return{
                    id:     data.id,
                    title:  data.title,
                    duration:   data.duaration,
                    genre:  data.genre,
                    description: data.description,
                    year:   data.year,
                    director:   data.director,
                    urlImg:     data.urlImg,
                    urlTrailer: data.urlTrailer
                }
        });*/
    }

    render(){
        const {id, title, duaration, genre, description, year, director, urlImg, urlTrailer} = this.state;
        return (
            <div className="col-md-6">
                <div className="row">
                    <div className="col-md-3">
                        <label>Название</label>
                    </div>
                    <div className="col-md-3">
                        <label>{title}</label>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-3">
                        <label>Жанр</label>
                    </div>
                    <div className="col-md-3">
                        <label>{genre}</label>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-3">
                        <label content="Опсиание"/>
                    </div>
                    <div className="col-md-3">
                        <label>{description}</label>
                    </div>
                </div>
                <div className="row">
                    <div className="offset-md2 col-md2">
                        <button className="btn-primary" onClick={this.onButtonClick} >Получить фильм</button>
                    </div>
                </div>
            </div>
        )
    }
}
