import React from 'react';

import image from './image-not-found.png';
import './FilmMiniView.css';

    const FilmMiniView = (props) =>{

        return (
            <div>
                {props.film.urlImg == null ? (
                    <div className="FilmMiniView" onClick={props.FilmViewClick}>
                        <img id="FilmImage" src={image}/>
                        <label id="TitleValue">{props.film.title}</label>
                    </div>
                ):(
                    <div className="FilmMiniView" onClick={props.FilmViewClick}>
                        <img id="FilmImage" src={props.film.urlImg}/>
                        <label id="TitleValue">{props.film.title}</label>
                    </div>
                )}
            </div>

        )
    };

    export default FilmMiniView;
