import React from 'react';
import ApiService from '../../services/FilmApiService';

export  default class RandomFilmView extends  React.Component{

    ApiService = new ApiService();


    /*state={
        id:     null,
        title:  null,
        duration:   null,
        genre:  null,
        description: null,
        year:   null,
        director:   null,
        urlImg:     null,
        urlTrailer: null
    };*/

    /*constructor(){
        super();
        this.updateFilm();

    }*/

    /*updateFilm(){
        this.ApiService
            .getRandomFilm()
            .then(this.onLoadedFilm())
    }*/

    onButtonClick = () => {
        this.props = this.ApiService.getRandomFilm();
    }

    /*async getFilm(e){
        //Убирает перезагрузку страницы
        e.preventDefault();
        //Запрос к бекенду
        const api_url =  await  fetch(`${BasePath}/api/${Controller}/${Id}`)
            .then(res => res.json())
            .then(body => {
                this.state.setState({
                    id: body.id,
                    title: body.title,
                    duaration: body.duaration,
                    genre: body.genre,
                    description: body.description,
                    year: body.year,
                    director: body.director,
                    urlImg: body.urlImg,
                    urlTrailer: body.urlTrailer
                    }
                )
            })
            .catch(error => console.log(error));
        //const data = await api_url.json();
    }*/


    /*handleClick = () => {
        //Обработчик нажатия
    }*/

    render(){
        const {id, title, duaration, genre, description, year, director, urlImg, urlTrailer} = this.state;

        return (
            <div className="col-md-6">
                <div className="row">
                    <div className="col-md-3">
                        <label>Название</label>
                    </div>
                    <div className="col-md-3">
                        <label>{this.props.title}</label>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-3">
                        <label>Жанр</label>
                    </div>
                    <div className="col-md-3">
                        <label>{this.props.genre}</label>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-3">
                        <label>Описание</label>
                    </div>
                    <div className="col-md-3">
                        <label>{this.props.description}</label>
                    </div>
                </div>
                <div className="row">
                    <div className="col-offset-md2 col-md2">
                        <button className="btn-primary" onClick={this.onButtonClick} >Получить фильм</button>
                    </div>
                </div>
            </div>
        )
    }
}
